import { useState, useEffect } from "react";

export const useGetUserID = () => {
  const [userID, setUserID] = useState(null);

  useEffect(() => {
    const storedUserID = window.localStorage.getItem("userID");
    if (storedUserID) {
      setUserID(storedUserID);
    }
  }, []);

  return userID;
};
